    const MOD = 10 ** 9 + 7;
    const dp = Array.from({ length: n + 1 }, () => Array(7).fill(0));
    for (let i = 1; i <= 6; i++) {
        dp[1][i] = 1;
    }

    for (let i = 2; i <= n; i++) {
        for (let j = 1; j <= 6; j++) {
            for (let k = 1; k <= rollMax[j - 1]; k++) {
                if (i - k >= 1) {
                    for (let l = 1; l <= 6; l++) {
                        if (l !== j) {
                            dp[i][j] = (dp[i][j] + dp[i - k][l]) % MOD;
                        }
                    }
                }
            }
        }
    }

    let result = 0;
    for (let i = 1; i <= 6; i++) {
        result = (result + dp[n][i]) % MOD;
    }
    return result;
};

class Solution {
    public int dieSimulator(int n, int[] rollMax) {
        int MOD = 1000000007;
        long[][] dp = new long[n + 1][6];

        // Initialize the first roll
        for (int i = 0; i < 6; i++) {
            dp[1][i] = 1;
        }

        // Fill the dp table
        for (int i = 2; i <= n; i++) {
            for (int j = 0; j < 6; j++) {
                // Calculate the number of ways to roll the die
                for (int k = 0; k < 6; k++) {
                    if (k != j) {
                        dp[i][j] = (dp[i][j] + dp[i - 1][k]) % MOD;
                    } else {
                        // If rolling the same number, check the rollMax constraint
                        for (int m = 1; m <= rollMax[j] && m < i; m++) {
                            dp[i][j] = (dp[i][j] + dp[i - m][k]) % MOD;
                        }
                    }
                }
            }
        }

        // Sum up all the ways to roll the die after n rolls
        long result = 0;
        for (int i = 0; i < 6; i++) {
            result = (result + dp[n][i]) % MOD;
        }

        return (int) result;    
    }
}

