    def countSubgraphsForEachDiameter(self, n, edges):
        """
        :type n: int
        :type edges: List[List[int]]
        :rtype: List[int]
        """
        from collections import defaultdict

        # Build the adjacency list for the tree
        graph = defaultdict(list)
        for u, v in edges:
            graph[u].append(v)
            graph[v].append(u)

        def dfs(node, parent):
            max_depth = 0
            second_max_depth = 0

            for neighbor in graph[node]:
                if neighbor == parent:
                    continue
                depth = dfs(neighbor, node) + 1
                if depth > max_depth:
                    second_max_depth = max_depth
                    max_depth = depth
                elif depth > second_max_depth:
                    second_max_depth = depth

            return max_depth

        result = [0] * (n - 1)

        for i in range(1, 1 << n):
            nodes_in_subtree = []
            for j in range(n):
                if i & (1 << j):
                    nodes_in_subtree.append(j + 1)

            if len(nodes_in_subtree) < 2:
                continue

            # Check if the subtree is valid
            valid = True
            for u in nodes_in_subtree:
                for v in graph[u]:
                    if v not in nodes_in_subtree:
                        valid = False
                        break
                if not valid:
                    break

            if not valid:
                continue

            # Calculate the diameter of the subtree
            max_diameter = 0
            for node in nodes_in_subtree:
                max_diameter = max(max_diameter, dfs(node, -1))

            if max_diameter > 0:
                result[max_diameter - 1] += 1

        return result

class Solution {

    /**
     * For each possible diameter d (1 to n-1), count the number of connected subtrees
     * whose maximum distance between any two nodes is exactly d.
     * Uses bitmask to enumerate all subsets and BFS to check connectivity and compute diameter.
     * @param Integer $n
     * @param Integer[][] $edges
     * @return Integer[]
     */
    function countSubgraphsForEachDiameter($n, $edges) {
        // Build adjacency list (0-indexed)
        $adj = array_fill(0, $n, []);
        foreach ($edges as $e) {
            $u = $e[0] - 1;
            $v = $e[1] - 1;
            $adj[$u][] = $v;
            $adj[$v][] = $u;
        }

        $res = array_fill(0, $n - 1, 0);

        // Enumerate all subsets with at least 2 nodes
        for ($mask = 1; $mask < (1 << $n); $mask++) {
            if (self::bitCount($mask) < 2) continue;

            // Get nodes in this subset
            $nodes = [];
            for ($i = 0; $i < $n; $i++) {
                if ($mask & (1 << $i)) $nodes[] = $i;
            }

            // Check if the subset is connected using BFS
            $visited = [];
            $queue = [$nodes[0]];
            $visited[$nodes[0]] = true;
            $cnt = 1;
            while ($queue) {
                $cur = array_shift($queue);
                foreach ($adj[$cur] as $nei) {
                    if (($mask & (1 << $nei)) && !isset($visited[$nei])) {
                        $visited[$nei] = true;
                        $queue[] = $nei;
                        $cnt++;
                    }
                }
            }
            if ($cnt != count($nodes)) continue; // Not connected

            // Find the diameter of the subtree (two BFS)
            $farthest = self::bfsFarthest($nodes[0], $mask, $adj, $n);
            $diameter = self::bfsFarthest($farthest['node'], $mask, $adj, $n)['dist'];
            if ($diameter > 0 && $diameter <= $n - 1) {
                $res[$diameter - 1]++;
            }
        }
        return $res;
    }

    // Helper: Count number of set bits in mask
    private static function bitCount($x) {
        $count = 0;
        while ($x) {
            $count += $x & 1;
            $x >>= 1;
        }
        return $count;
    }

    // Helper: BFS to find farthest node and its distance in the subset
    private static function bfsFarthest($start, $mask, &$adj, $n) {
        $visited = array_fill(0, $n, false);
        $queue = [[$start, 0]];
        $visited[$start] = true;
        $farthest = ['node' => $start, 'dist' => 0];
        while ($queue) {
            list($cur, $d) = array_shift($queue);
            if ($d > $farthest['dist']) {
                $farthest = ['node' => $cur, 'dist' => $d];
            }
            foreach ($adj[$cur] as $nei) {
                if (($mask & (1 << $nei)) && !$visited[$nei]) {
                    $visited[$nei] = true;
                    $queue[] = [$nei, $d + 1];
                }
            }
        }
        return $farthest;
    }
}

